mapper.select:
SELECT bbsno, title, content, hits, DATE_FORMAT(regdate, '%Y-%m-%d') AS regdate, DATE_FORMAT(editdate, '%Y-%m-%d') AS editdate, (SELECT filepath FROM files WHERE fileno=(SELECT fileno FROM files WHERE refid=bbsno AND reftable='bbs' order by fname asc limit 0, 1)) AS filepath FROM bbs
type='N' AND membno=1 AND bbsno=#{bbsno}
AND title LIKE concat('%', #{title}, '%')

mapper.insert:
INSERT INTO bbs (
	type, title, content, hits, regdate, editdate, membno
) VALUES (
	'N', #{title}, #{content}, 0, now(), now(), 1
)

mapper.update:
UPDATE bbs SET
	title=#{title}, content=#{content}, editdate=now()
WHERE bbsno=#{bbsno}
