mapper.select:
SELECT (SELECT filepath FROM files WHERE fileno=(SELECT fileno FROM files WHERE refid=prodno AND reftable='product' order by fname asc limit 0, 1)) AS filepath, (SELECT thumbnailPath FROM files WHERE fileno=(SELECT fileno FROM files WHERE refid=prodno AND reftable='product' order by fname asc limit 0, 1)) AS thumbnailPath, subject, price, prodno, (SELECT nickname FROM member WHERE membno=seller) AS sellerNick FROM product WHERE prodcon=#{prodcon} and how={how}
order by regdate Desc
LIMIT #{offset}, #{listCount}

SELECT subject, price, p.prodno, c.cateno, c.name as catename, prodcon, how, tradecon, hits, seller, (SELECT nickname FROM member WHERE membno=seller) AS sellerNick, (SELECT filepath FROM files WHERE fileno=(SELECT fileno FROM files WHERE refid=p.prodno AND reftable='product' order by fname asc limit 0, 1)) AS filepath, (SELECT thumbnailPath FROM files WHERE fileno=(SELECT fileno FROM files WHERE refid=prodno AND reftable='product' order by fname asc limit 0, 1)) AS thumbnailPath FROM product p INNER JOIN category c ON c.cateno=p.cateno
tradecon='J'
AND c.cateno = #{cateno}
ORDER BY regdate Desc
LIMIT #{offset}, #{listCount}

SELECT (SELECT filepath FROM files WHERE fileno=(SELECT fileno FROM files WHERE refid=prodno AND reftable='product' order by fname asc limit 0, 1)) AS filepath, (SELECT thumbnailPath FROM files WHERE fileno=(SELECT fileno FROM files WHERE refid=prodno AND reftable='product' order by fname asc limit 0, 1)) AS thumbnailPath, subject, price, prodno, (SELECT nickname FROM member WHERE membno=seller) AS sellerNick FROM product WHERE cateno=#{cateno} AND tradecon='J'
AND prodno!=#{prodno}
ORDER BY regdate DESC
LIMIT #{offset}, #{listCount}

SELECT (SELECT filepath FROM files WHERE fileno=(SELECT fileno FROM files WHERE refid=prodno AND reftable='product' order by fname asc limit 0, 1)) AS filepath, (SELECT thumbnailPath FROM files WHERE fileno=(SELECT fileno FROM files WHERE refid=prodno AND reftable='product' order by fname asc limit 0, 1)) AS thumbnailPath, subject, price, prodno, (SELECT nickname FROM member WHERE membno=seller) AS sellerNick FROM product WHERE seller=#{seller} AND tradecon='J'
AND prodno!=#{prodno}
ORDER BY regdate DESC
LIMIT #{offset}, #{listCount}
