mapper.select:
SELECT prodno, tradecon, seller
FROM product
WHERE seller=#{seller}

mapper.update:
UPDATE product SET
	 tradecon= 'W'
WHERE prodno=#{prodno}

mapper.select:
SELECT COUNT(*) FROM product
seller = #{seller} AND tradecon = #{tradecon}
AND (subject LIKE concat('%', #{subject}, '%') OR prodno in (SELECT DISTINCT prodno FROM hashtag WHERE tagname LIKE concat('%', #{subject}, '%')))

SELECT COUNT(*) FROM membprod mp INNER JOIN product p ON mp.prodno=p.prodno
membno = #{seller}
AND (p.subject LIKE concat('%', #{subject}, '%') OR p.seller=(SELECT membno FROM member WHERE membno=seller AND nickname LIKE concat('%', #{subject}, '%')) OR p.prodno in (SELECT DISTINCT prodno FROM hashtag WHERE tagname LIKE concat('%', #{subject}, '%')))

SELECT prodno, cateno, subject, price, fee, detail, DATE_FORMAT(regdate, '%Y-%m-%d %H:%i:%S') AS regdate, prodcon, how, tradecon, hits, seller, (SELECT nickname FROM member WHERE membno=seller) AS sellerNick, DATE_FORMAT(editdate, '%Y-%m-%d %H:%i:%S') AS editdate, (SELECT filepath FROM files WHERE fileno=(SELECT fileno FROM files WHERE refid=prodno AND reftable='product' order by fname asc limit 0, 1)) AS filepath FROM product
seller = #{seller} AND tradecon = #{tradecon}
AND (subject LIKE concat('%', #{subject}, '%') OR prodno in (SELECT DISTINCT prodno FROM hashtag WHERE tagname LIKE concat('%', #{subject}, '%')))
ORDER BY prodno DESC
LIMIT #{offset}, #{listCount}

mapper.update:
UPDATE product SET
	 tradecon= #{tradecon}
WHERE prodno=#{prodno}

UPDATE product SET
	 cateno= #{cateno}
WHERE prodno=#{prodno}

mapper.select:
SELECT prodno, p.cateno, c.name AS catename, subject, (SELECT nickname FROM member WHERE membno=seller) AS sellerNick, DATE_FORMAT(regdate, '%Y-%m-%d %H:%i:%S') AS regdate FROM product p INNER JOIN category c ON c.cateno=p.cateno
p.cateno = #{cateno}
AND (subject LIKE concat('%', #{subject}, '%') OR seller=(SELECT membno FROM member WHERE membno=seller AND nickname LIKE concat('%', #{subject}, '%')) OR prodno in (SELECT DISTINCT prodno FROM hashtag WHERE tagname LIKE concat('%', #{subject}, '%')))
ORDER BY regdate DESC
ORDER BY subject ASC
LIMIT #{offset}, #{listCount}

SELECT prodno, subject, seller
FROM product
WHERE prodno=#{prodno}
