mapper.select:
SELECT recono, r.how, p.prodno, p.subject, p.price, r.refund, r.tradecon, p.seller, r.buyer, (SELECT nickname FROM member WHERE membno=buyer) AS nickname, (SELECT filepath FROM files WHERE fileno=(SELECT fileno FROM files WHERE refid=r.prodno AND reftable='product' order by fname asc limit 0, 1)) AS filepath FROM record r INNER JOIN product p ON r.prodno=p.prodno WHERE p.tradecon='W' AND r.tradecon='J' AND r.refund in('N', 'J', 'X') AND p.seller=#{seller}
ORDER BY r.prodno DESC
LIMIT #{offset}, #{listCount}

SELECT recono, r.how, p.prodno, p.subject, p.price, r.refund, r.tradecon, p.seller, r.buyer, (SELECT nickname FROM member WHERE membno=buyer) AS nickname, r.confirmdate, r.name, r.tel, r.address, r.pay, (SELECT filepath FROM files WHERE fileno=(SELECT fileno FROM files WHERE refid=r.prodno AND reftable='product' order by fname asc limit 0, 1)) AS filepath, (SELECT revino FROM review WHERE prodno=r.prodno AND sender=#{seller}) AS revino FROM record r INNER JOIN product p ON r.prodno=p.prodno INNER JOIN member m ON r.buyer=m.membno WHERE NOT r.tradecon='J' AND NOT r.refund='J' AND p.seller=#{seller}
ORDER BY r.prodno DESC
LIMIT #{offset}, #{listCount}

mapper.update:
UPDATE record SET
    tradecon='W', confirmdate=now(), buyer=#{buyer}, name=#{nickname}
WHERE prodno=#{prodno} AND tradecon='J'

UPDATE product SET
    tradecon='W'
WHERE prodno=#{prodno}

UPDATE record SET
    tradecon='W', confirmdate=now(), buyer=#{buyer}
WHERE recono=#{recono} AND tradecon='J' AND not refund='J'

UPDATE product SET
    tradecon='J'
WHERE prodno=#{prodno}

UPDATE record SET
    refund=#{refund}, tradecon=#{tradecon}
WHERE recono=#{recono}

