mapper.update:
UPDATE record SET
    tradecon='W', confirmdate=now(), buyer=#{buyer}, name=#{nickname}
WHERE recono=#{recono} AND tradecon='J'

mapper.insert:
INSERT INTO record (
    prodno, buyer, tradecon, refund, how, confirmdate, name, tel, address, pay 
) VALUES (
    #{prodno}, #{buyer}, 'J', 'N', 'T', now(), #{name}, #{tel}, #{address}, #{pay} 
)

mapper.select:
SELECT r.recono, p.price,p.prodno, m.nickname, DATE_FORMAT(r.confirmdate, '%Y-%m-%d') AS confirmdate, r.how, r.tradecon, r.refund FROM record r INNER JOIN product p ON r.prodno=p.prodno INNER JOIN member m ON r.buyer= m.membno
ORDER BY r.confirmdate DESC
LIMIT #{offset}, #{listCount}
